import React, { Component } from "react";

class Appugly extends Component {
    state = {
        count: 0
    };

    render() {
        const { count } = this.state;
        const handleClick = () => {
            this.setState(count+1);
        };

        return (
            <div>
                You have clicked {count} times!
                <button onClick={handleClick}>Test me</button>
            </div>
        )
    }

}

export default Appugly;
